import React from "react";

function Card({ title, price, imageUrl, onFavorite}) {
    const [isAdded, setIsAdded] = React.useState(false);

    const onClickPlus = () => {
        onPlus();
        setIsAdded(!isAdded);
    }

    console.log(isAdded);

    return(
        <div className="card">
            <div className="favorite">
                <img onClick={onFavorite} src="/img/unliked.svg" />
            </div>
             <img width={133} height={112} src={imageUrl}/>
            <p>{title}</p>
            <div className="card-inner">
                <div className="card-price">
                  <p>Цена:</p>
                  <b>{price} руб.</b>
                </div>
                <button  
                    onClick={onClickPlus}>
                     <img 
                        width={32} 
                        height={32} 
                        src={isAdded ? '/img/cheked.svg' : '/img/btn-plus.svg'} /> 
                </button>
            </div>
        </div>
    );
}

export default Card
