import Card from './conponents/Card'
import Header from './conponents/Header'
import Drawer from './conponents/Drawer'
import React from 'react'
import axios from 'axios'


function App() {
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');
  const [cartOpened, setCartOpened] = React.useState(false);

  React.useEffect(() =>{
    fetch('https://61eebe07d593d20017dbb107.mockapi.io/items')
      .then((res) => {
        return res.json();
      })
      .then((json) => {
       setItems(json);
      });

      axios.get('https://61eebe07d593d20017dbb107.mockapi.io/items').then((res) => {
        setItems(res.data)
      });
  }, [])

  const onAdToCart = (obj) => {
    setCartItems(prev => [...prev, obj])
  }

  const onChangeSearchInput = (event) => {
    setSearchValue(event.target.value);
  }

  return (
    <div className="wrapper">
      {cartOpened ? 
      <Drawer 
        items={cartItems} 
        onClose={() => setCartOpened(false)} 
      /> : null
      }
      <Header onClickCart={() => setCartOpened(true)} />
      <div className="content">
        <div className="content-header">
          <h1>{searchValue ? `Поиск по запросу: "${searchValue}"` : 'Все кроссовки'}</h1>
          <div className="searchBlock">
            <img src="/img/search.svg" />
            <input onChange={onChangeSearchInput} value={searchValue} placeholder="Поиск..." />
            {searchValue && (
                <img 
                  onClick={() => setSearchValue('')} 
                  className="removeBtn" 
                  src="/img/btn-remove.svg" 
                  alt="Clear" 
                />)
            }
          </div>
        </div>
        <div className="card-main">
          { items
            .filter((item) => item.title.toLowerCase().includes(searchValue.toLowerCase()))
            .map( (item, index) =>( 
              <Card 
                key={index}
                title={item.title}
                price={item.price}
                imageUrl={item.imageUrl}
                onPlus={(obj) => onAdToCart(obj)}
              />
            ))}
        </div>
      </div>
    </div>
  );
}

export default App;
