import Header from './conponents/Header'
import Drawer from './conponents/Drawer'
import Home from './pages/Home'
import Favorites from './pages/Favorites'
import React from 'react'
import { Link, Route, Routes } from 'react-router-dom'
import axios from 'axios'


function App() {
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([]);
  const [favorites, setFavorites] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState('');
  const [cartOpened, setCartOpened] = React.useState(false);

  React.useEffect(() => {
    axios.get('https://61eebe07d593d20017dbb107.mockapi.io/items').then((res) => {
      setItems(res.data)
    });

    axios.get('https://61eebe07d593d20017dbb107.mockapi.io/cart').then((res) => {
      setCartItems(res.data)
    });

    axios.get('https://61eebe07d593d20017dbb107.mockapi.io/favorites').then((res) => {
      setFavorites(res.data)
    });
  }, [])

  const onAddToCart = (obj) => {
    console.log(obj)
    if (cartItems.find((item) => Number(item.id)  ===  Number(obj.id) )){
      axios.post('https://61eebe07d593d20017dbb107.mockapi.io/cart', obj);
      setCartItems(prev => prev.filter(item => Number(item.id) != Number(obj.id)));
    }
    axios.post('https://61eebe07d593d20017dbb107.mockapi.io/cart', obj);
    setCartItems((prev) => [...prev, obj]);
  }

  const onAddToFavorite = async (obj) => {
    try {
      if (favorites.find((favObj) => favObj.id  === obj.id )){
        axios.delete(`https://61eebe07d593d20017dbb107.mockapi.io/favorites/${obj.id}`);
        setFavorites((prev) => prev.filter((item) => item.id !== obj.id));
      } else {
        const { data } = await axios.post('https://61eebe07d593d20017dbb107.mockapi.io/favorites', obj)
        setFavorites((prev) => [...prev, data]);
      }
    } catch (error) {
      alert("Не удалось добавить!");
    }
  }

  const onRemoveCart = (id) => {
    axios.delete(`https://61eebe07d593d20017dbb107.mockapi.io/cart/${id}`)
    setCartItems((prev) => prev.filter((item) => item.id != id));
  }

  const onChangeSearchInput = (event) => {
    setSearchValue(event.target.value);
  }

  return (
    <div className="wrapper">
      {cartOpened ?
        <Drawer
          items={cartItems}
          onClose={() => setCartOpened(false)}
          onRemove={onRemoveCart}
        /> : null
      }
      <Header onClickCart={() => setCartOpened(true)} />
      <Routes>
        <Route
          path="/"
          element={
            <Home
              items={items}
              searchValue={searchValue}
              setSearchValue={setSearchValue}
              onChangeSearchInput={onChangeSearchInput}
              onAddToCart={onAddToCart}
              onAddToFavorite={onAddToFavorite}
            />}
          exact />

        <Route
          path="/favorites"
          element={
            <Favorites
              items={favorites}
              onAddToFavorite={onAddToFavorite}
            />}
          exact />
      </Routes>

    </div>
  );
}

export default App;
