import Header from './conponents/Header'
import Drawer from './conponents/Drawer'
import React from 'react'





function App() {
  const [items, setItems] = React.useState([]);
  const [cartItems, setCartItems] = React.useState([
    {
      "title": "Мужские Кроссовки Nike Blazer Mid Suede",
      "price": 12999,
      "imageUrl": "/img/card-1.jpg"
     },
     {
      "title": "Мужские Кроссовки Nike Air Max 270",
      "price": 15699,
      "imageUrl": "/img/card-2.jpg"
     },
     {
      "title": "Мужские Кроссовки Nike Blazer Mid Suede",
      "price": 8499,
      "imageUrl": "/img/card-3.jpg"
     },
  ]);
  const [cartOpened, setCartOpened] = React.useState(false);

  React.useEffect(() =>{
    fetch('https://61eebe07d593d20017dbb107.mockapi.io/items')
      .then((res) => {
        return res.json();
      })
      .then((json) => {
       setItems(json);
      });
  }, [])

  const onAdToCart = () => {

  }

  return (
    <div className="wrapper">
      {cartOpened ? <Drawer cartItems={cartItems} onClose={() => setCartOpened(false)} /> : null}
