import Info from "./Info";
import React from "react";
import AppContext from "../context";
import axios from 'axios'

const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms)) 

function Drawer({onClose, items = [], onRemove }) {
  const { cartItems, setCartItems } = React.useContext(AppContext);
  const [orderId, setOrderId] = React.useState(false);
  const [isOrderComplete, setIsOrderComplete] = React.useState(false);
  const totalPrice = cartItems.reduce((sum, obj) => obj.price + sum, 0)

  const onClickOrder = async () => {
    try {
      const { data } = await axios.post('https://61eebe07d593d20017dbb107.mockapi.io/orders', {
        items: cartItems,
      });
      setOrderId( data.id );
      setIsOrderComplete(true);
      setCartItems([]);

      for(let i = 0; i < cartItems.length; i++){
        const item = cartItems[i];
        await axios.delete('https://61eebe07d593d20017dbb107.mockapi.io/card/' + item.id);
        await delay(1000);
      }
    } catch (error) {

    }
  }

    return(
        <div className="overlay" >
        <div className="drawer">
          <h2>Kорзина <img onClick={onClose} className="removeBtn" src="/img/btn-remove.svg" alt="Remove" /></h2>

          {
            items.length > 0 ? (
              <div>
                <div className="items">
              {items.map((obj) => (
                <div key={obj.id} className="cartItem">
                <img 
                  className="cartImg" 
                  width={80} 
                  height={80} 
                  src={obj.imageUrl} 
                />
                <div className="cartBlock">
                  <p>{obj.title}</p>
                  <b>{obj.price} руб. </b>
                </div>
                <img onClick={() => onRemove(obj.id)} className="removeBtn" src="/img/btn-remove.svg" alt="Remove" />
              </div> 
              ))}  
              </div> 
               <ul className="cartTotalBlock">
               <li>
                 <span>Итого: </span>
                 <div></div>
                 <b>{ totalPrice } руб. </b>
               </li>
               <li>
                 <span>Налог 5%: </span>
                 <div></div>
                 <b>{ totalPrice / 100 * 5} руб. </b>
               </li>
               <button onClick={ onClickOrder } className="greenButton">
                  Оформить заказ
                  <img
                   src="/img/arrow.svg"
                  />
               </button>
             </ul>
              </div>
            ) : (
              <Info
                title={isOrderComplete ?
                    "Заказ оформлен!" :
                    'Корзина пустая'}
                image={isOrderComplete ? '/img/complete-order.svg' :'/img/empty.svg'}
                description={isOrderComplete ?
                    `Ваш заказ #${orderId} скоро будет передан курьерской доставке`: 
                    'Добавьте хотя бы одну пару кроссовок, чтобы сделать заказ.'}
              />
            )
          }

        </div>
      </div>
    );
}

export default Drawer;
